$(document).ready(function(){$(".prices-slider").length&&$(".prices-slider").slick({infinite:!0,arrows:!0,speed:500,slidesToShow:3,slidesToScroll:3,responsive:[{breakpoint:600,settings:{slidesToShow:1,slidesToScroll:1,infinite:!0}}]})});
var inputs=document.querySelectorAll(".checkbox > input");inputs.forEach(function(e){e.addEventListener("change",function(e){var t=e.target.closest(".form__item").querySelector(".form__descr");document.querySelectorAll(".form__descr").forEach(function(e){e.classList.remove("active"),t.classList.add("active")})})});